        $('#gotoNextPeriodIcon').css('margin-left','5px');
        $('#gotoPrevPeriodIcon').css('vertical-align','');
        $('#gotoPrevPeriodIcon').attr('align','');
        $('#gotoNextPeriodIcon').css('vertical-align','');
        $('#gotoNextPeriodIcon').attr('align','');

